$(document).on('change', '[name=checkall]', function (e) {
	$(this).parents('table').find('[name=checkrow]').prop('checked', $(this).prop('checked'));
	var content = '';
	$.each($('[name=checkrow]:checked'), function() {
		content += '<input type="hidden" class="check-uuid" name="uuid[]" value="'+$(this).val()+'">';
	});
	$('.selected-form').find('.check-uuid').remove();
	$('.selected-form').append(content);
});
$(document).on('change', '[name=checkrow]', function (e) {
	if ($(this).is(':checked')) {
		$('.selected-form').append('<input type="hidden" class="check-uuid" name="uuid[]" value="'+$(this).val()+'">');
	}
	else {
		$('.selected-form').find('[value='+$(this).val()+']').remove();
	}
});
$(document).on('click', '.btn-trigger-selected', function (e) {
	e.preventDefault();
	if ($('[name=checkrow]:checked').length > 0) {
		var $this = $(this);
		Swal.fire({
	        title: 'Are you sure?',
	        text: "",
	        icon: 'warning',
	        showCancelButton: true,
	        confirmButtonColor: '#3085d6',
	        cancelButtonColor: '#d33',
	        confirmButtonText: 'Yes, do it!'
	    }).then((result) => {
	    	if (result.value) {
		       	$('.selected-form').attr('action', $this.data('url')).submit();
	    	}
	    });
	}
	else {
		notify_bar('danger', 'Please select at least one before proceeding.');
	}
});