$(document).on('click', '.download-image', function (e) {
    e.preventDefault();
    $('.unsplash-overlay').show();
    notify_forever('info', 'Please wait', 'Download in process.', '<i class="pg-icon">alert_info</i>');
    var $parent = $(this).parents('.unsplash-options');
    $.ajax({
        url: save_url,
        type: 'POST',
        data: {
            id : $parent.find('.image-id').val(),
            url : $parent.find('.image-url').val(),
            author : $parent.find('.image-author').val(),
            title : $parent.find('.image-title').val(),
            caption : $parent.find('.image-caption').val()
        },
        success: function(response) {
            if (response.status == 'success') {
                $('.unsplash-overlay').hide();
                notify_bar('success', 'Image has been downloaded.');      
            }
            else {
                notify_reload('danger', 'Something went wrong. please try again later.');
            }
        },
        statusCode: {
            500: function () { notify_reload('danger', 'Something went wrong. please try again later.'); },
            422: function () { notify_reload('danger', 'Something went wrong. please try again later.'); },
            403: function () { notify_reload('danger', 'Something went wrong. please try again later.'); }
        }  
    });
});