$(document).on('click', '.btn-quick-edit', function (e) {
    e.preventDefault();
    $this = $(this);
    $uuid = $this.data('uuid');
    $.ajax({
        url: url_product_get_detail,
        type: 'POST',
        data: { uuid: $uuid },
        async: false,
        success: function (response) {
            if (response.status) {
                var $content = response.html;
                var $modal = $('#modal-quick-edit-form');
                $modal.html($content);
            }
            else{
                notify_bar('danger', 'There is no variation for this product');
            }
        }
    })
});