var $sort = $(".sortable .sortable-column");
$sort.sortable({
    connectWith: ".sortable .sortable-column",
    handle: ".card-header",
    forcePlaceholderSize: true,
    tolerance: 'pointer',
    forceHelperSize: true,
    revert: true,
    helper: 'original',
    opacity: 0.8,
    iframeFix: false
});
$sort.on('sortupdate', function() {
    $.ajax({
        data: $sort.sortable('serialize'),
        url : $sort.data('url'),
        type: 'post',
        async: false,
        success : function (response) {
            var data = $.parseJSON(response);
            if (data == true) { 
                notify_bar('success', 'The display order has been updated.'); 
            } 
            else {
                notify_bar('danger', 'Sorry, could not update the display order.');
            }
        },
        error : function (response) {
            notify_bar('danger', 'Sorry, something went wrong. Please try again later');
        }
    });
});
$sort.trigger('sortupdate');
$sort.disableSelection();